import React, { useState } from "react";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";
import { Line } from "react-chartjs-2";

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

interface IRecordData {
  data: Date;
  value: Number;
}
interface IProps {
  data: IRecordData[];
  title: String;
}

const Graphic: React.FC<IProps> = (props) => {
  const [datas, setDatas] = useState(props.data);
  console.log(datas);
  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: "top" as const,
      },
      title: {
        display: true,
        text: "Gaz Luna Martie",
      },
    },
  };
  const labels = props.data.map(
    (d) => `${d.data.getDate()}/${d.data.getMonth()}`
  );

  const data = {
    labels,
    datasets: [
      {
        label: "Gaz",
        data: props.data.map((d) => d.value),
        borderColor: "rgb(255, 99, 132)",
        backgroundColor: "rgba(255, 99, 132, 0.5)",
      },
    ],
  };
  return <Line height={50} width={100} options={options} data={data} />;
};

export default Graphic;
