import React, { useState } from "react";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  Filler,
} from "chart.js";
import { Line } from "react-chartjs-2";

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  Filler
);

interface IRecordData {
  data: Date;
  value: Number;
}
interface IProps {
  data: IRecordData[];
  title: String;
  color: String;
}

const Graphic: React.FC<IProps> = (props) => {
  const [datas, setDatas] = useState(props.data);
  console.log(datas);
  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: "top" as const,
      },
      title: {
        display: true,
        text: `${props.title} luna Martie`,
      },
    },
    type: "line",
    labels: false,
    scales: {
      //   x: {
      //     display: true,
      //     title: {
      //       display: true,
      //       text: "Day",
      //     },
      //   },
      //   y: {
      //     display: true,
      //     title: {
      //       display: true,
      //       text: "Value",
      //     },
      //   },
    },
  };
  const labels = props.data.map(
    (d) => `${d.data.getDate()}/${d.data.getMonth()}`
  );

  const data = {
    labels,
    datasets: [
      {
        label: props.title.toString(),
        data: props.data.map((d) => d.value),
        borderColor: props.color.toString(),
        backgroundColor: props.color.toString(),
        fill: true,
      },
    ],
  };
  return <Line height={80} width={100} options={options} data={data} />;
};

export default Graphic;
